import React, { Component } from "react";
import Pagination from "./common/pagination";
import { paginate } from "../utils/paginate";
import ListGroup from "./common/listGroup";
import { getGenres } from "../services/fakeGenreService";
import MoviesTable from "./moviesTable";
import _ from "lodash";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

//imrc cc

class Movies extends Component {
  state = {
    movies: [],
    genres: [],
    pageSize: 4,
    currentPage: 1,
    sortColumn: { path: "title", order: "asc" },
    hi: {hi: console.log("this is called again")},
    id: "",
    title: "",
    genre: "",
    stock: "",
    rate: "",
    liked: false,

  };



  async componentDidMount() {
    

      
      fetch('https://localhost:7086/api/Movie').then(resp=>resp.json())
      .then(resp=>
   
    this.setState({
      movies: resp})
    );
       
    console.log("in component1", this.state);  
      
    const genres = [{ _id: "", name: "All Genres" }, ...getGenres()];
    
    
    this.setState({
      ...this.state,
      genres: genres,
    });
    console.log("in component2", this.state);
  }


  

  handleDelete = (movie) => {
    console.log("movie deleted", movie);
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies: movies }); //key value
  };

  handleLike = (movie) => {
    console.log("movie liked", movie);
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies: movies });
  };

  handlePageChange = (page) => {
    console.log(page);
    this.setState({ currentPage: page });
  };

  handleGenreSelect = (genre) => {
    this.setState({ selectedGenre: genre, currentPage: 1 });
  };

  handleSort = sortColumn => {
    this.setState({ sortColumn });
  };

  handleAdd = () =>
  {
    const movie = {
      "id": "34783975",
      "title": "Step Brothers",
      "genre": "Comedy",
      "numberInStock": 4,
      "dailyRentalRate": 2.5,
      "isLiked": true
    };
    console.log("movie",this.movie);
    // fetch('https://localhost:7086/api/Movie', this.movie);
    // this.setState({...this.state,moives: [...this.state.moives, this.moive]})
    // console.log("hi");
    
    
   
  }

  render() {
    const { length: count } = this.state.movies;
    const {
      pageSize,
      currentPage,
      sortColumn,
      movies: allMovies,
      selectedGenre,
    } = this.state;

    if (count === 0) return <p>There are no movies in the database</p>;
    console.log("selected genre -", selectedGenre);

    const filtered =
      selectedGenre && selectedGenre.name !== "All Genres"
        ? allMovies.filter((m) => m.genre === selectedGenre.name)
        : allMovies;

    const sorted = _.orderBy(filtered, [sortColumn.path], [sortColumn.order]);

    const movies = paginate(sorted, currentPage, pageSize, selectedGenre);

    return (
      <div className="row">
        <div className="col-3">
          <ListGroup
            items={this.state.genres}
            onItemSelect={this.handleGenreSelect}
            selectedItem={this.state.selectedGenre}
          ></ListGroup>
        </div>
        <div className="col" >
          <div className="row" ><p>Showing {filtered.length} movies in the database   </p>
          <Popup style={{marginTop: '3em'}} trigger=
                {<button onClick={()=>{handleAdd()}} className="btn btn-primary btn-sm" style = {{marginLeft: '30.2em', marginBottom: '0.7em'}}>Add Movie</button>}>
                   <form>
                    <>Id</>
          <input
           
            onChange={(e) => this.state.id=e.target.value}
            label="Id"
          />
          <>Title</>
          <input
           
            onChange={(e) => this.state.title=e.target.value}
            label="Title"
          />
          <>Genre</>
          <input
           
            onChange={(e) => this.state.genre=e.target.value}
            label="Genre"
          />
          <>Stock</>
          <input
            
           
            onChange={(e) => this.state.stock=e.target.value}
            label="Stock"
          />
          <>Liked</>
          <input
           
            onChange={(e) => this.state.liked=e.target.value}
            label="Liked"
          />
          <button type="submit"> Submit </button>
        </form>
            </Popup>
            </div>
          
          
         
          <MoviesTable
            movies={movies}
            sortColumn={sortColumn}
            onLike={this.handleLike}
            onDelete={this.handleDelete}
            onSort={this.handleSort}
          ></MoviesTable>
          {/* <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Genre</th>
                <th>Stock</th>
                <th>Rate</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {movies.map(
                //this is the local movies instance
                (movie) => (
                  <tr key={movie._id}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.numberInStock}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>
                      <Like
                        liked={movie.liked}
                        onClickToggle={() => this.handleLike(movie)}
                      ></Like>
                    </td>
                    <td>
                      <button
                        onClick={() => this.handleDelete(movie)}
                        className="btn btn-danger btn-sm"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                )
              )}
            </tbody>
          </table> */}
          <Pagination
            itemsCount={filtered.length}
            pageSize={pageSize}
            onPageChange={this.handlePageChange}
            currentPage={currentPage}
          ></Pagination>
        </div>
      </div>
    );
  }
}

export default Movies;
