label="Liked"
/>
<button onClick={() =>  {
    console.log("hi");
    let liked = false;
    if (this.state.liked == "true") {
      liked = true;
    };
    if (this.state.liked == "false") {
      liked = false;
    };
    const data={
      "id": this.state.id,
      "title": this.state.title,
      "genre": this.state.genre,
      "numberInStock": this.state.stock,
      "dailyRentalRate": this.state.rate,
      "isLiked": liked,
    };
